// Helper to check configuration status
bool isWiFiConfigured() {
    wifiPrefs.begin("wifi-config", true);
    bool configured = wifiPrefs.getBool("configured", false);
    wifiPrefs.end();
    return configured;
}

// Helpers to get stored values
String getStoredSSID() {
    wifiPrefs.begin("wifi-config", true);
    String s = wifiPrefs.getString("ssid", "");
    wifiPrefs.end();
    return s;
}

String getStoredPass() {
    wifiPrefs.begin("wifi-config", true);
    String p = wifiPrefs.getString("password", "");
    wifiPrefs.end();
    return p;
}

String getStoredBusiness() {
    wifiPrefs.begin("wifi-config", true);
    String b = wifiPrefs.getString("business_name", "");
    wifiPrefs.end();
    return b;
}

// Handler for Root URL
// Handler for Captive Portal detection
void handleCaptivePortal() {
    webServer.sendHeader("Cache-Control", "no-cache, no-store, must-revalidate");
    webServer.sendHeader("Pragma", "no-cache");
    webServer.sendHeader("Expires", "-1");
    webServer.sendHeader("Connection", "close");
    // Send 200 OK with a redirect body - more effective for some Android versions than a 302 header
    String body = "<!DOCTYPE html><html><head>";
    body += "<meta http-equiv=\"refresh\" content=\"0;url=http://www.cavlineglobal.com/\">";
    body += "<script>window.location.href=\"http://www.cavlineglobal.com/\";</script>";
    body += "</head><body><a href=\"http://www.cavlineglobal.com/\">Click here to connect</a></body></html>";
    webServer.send(200, "text/html", body);
}

void handleRoot() {
    // Only perform a WiFi scan if we don't have results yet OR if explicitly requested
    // This makes initial portal opening much faster
    if (scannedNetworks <= 0 || webServer.hasArg("scan")) {
        scannedNetworks = WiFi.scanNetworks();
        for (int i = 0; i < scannedNetworks && i < 20; i++) {
            scannedSSID[i] = WiFi.SSID(i);
        }
    }

    // Initiate chunked transfer
    webServer.setContentLength(CONTENT_LENGTH_UNKNOWN);
    webServer.sendHeader("Cache-Control", "no-cache, no-store, must-revalidate");
    webServer.send(200, "text/html", "");

    // Stream the first part (header + logo + start of form)
    webServer.sendContent_P(index_html_head);

    // Build and stream SSID options dynamically
    if (scannedNetworks <= 0) {
        webServer.sendContent("<option>No networks found</option>");
    } else {
        for (int i = 0; i < scannedNetworks && i < 20; i++) {
            // Send each option in smaller chunks if needed, but here it is fine as a single String per option
            String option = "<option value=\"" + scannedSSID[i] + "\">" + scannedSSID[i] + "</option>";
            webServer.sendContent(option);
        }
    }

    // Stream the rest of the HTML (rest of form + footer)
    webServer.sendContent_P(index_html_tail);

    // Finalize the chunked response
    webServer.sendContent("");
}


// Handler for Save
void handleSave() {
    String ssid = webServer.arg("ssid");
    String password = webServer.arg("password");
    String business_name = webServer.arg("business_name");
    
    if (ssid.length() > 0) {
        wifiPrefs.begin("wifi-config", false);
        wifiPrefs.putString("ssid", ssid);
        wifiPrefs.putString("password", password);
        wifiPrefs.putString("business_name", business_name);
        wifiPrefs.putBool("configured", true);
        wifiPrefs.end();
        
        String html = "<!DOCTYPE html><html><body style='background:#071218;color:#e6eef3;font-family:sans-serif;text-align:center;padding-top:50px;'>";
        html += "<h2>Configuration Saved</h2><p>The device is restarting to connect to your network.</p></body></html>";
        webServer.send(200, "text/html", html);
        delay(500);
        
        // Clean AP shutdown before restart
        dnsServer.stop();
        webServer.stop();
        WiFi.softAPdisconnect(true);
        delay(1000);
        ESP.restart();
    } else {
        webServer.send(200, "text/plain", "Error: SSID required");
    }
}

// Setup Provisioning Mode
void setupProvisioning(String apName) {
    WiFi.mode(WIFI_AP);
    
    // Scan networks ONLY ONCE when AP starts
    // Use synchronous scan for simplicity in provisioning mode
    scannedNetworks = WiFi.scanNetworks();
    if (scannedNetworks > 20) scannedNetworks = 20; 
    for (int i = 0; i < scannedNetworks; i++) {
        scannedSSID[i] = WiFi.SSID(i);
    }
    
    // Use provided UID as AP Name
    WiFi.softAP(apName.c_str());
    
    // Disable power saving for better responsiveness
    WiFi.setSleep(false);
    
    // Web Server Routes
    webServer.on("/", handleRoot);
    webServer.on("/save", handleSave);
    
    // Captive Portal Handlers - Redirect all probes to the main domain
    webServer.on("/generate_204", handleCaptivePortal);       // Android
    webServer.on("/gen_204", handleCaptivePortal);            // Android
    webServer.on("/hotspot-detect.html", handleCaptivePortal); // iOS/macOS
    webServer.on("/connecttest.txt", handleCaptivePortal);    // Windows
    webServer.on("/success.txt", handleCaptivePortal);        // Firefox
    webServer.on("/ncsi.txt", handleCaptivePortal);           // Windows
    webServer.on("/check_network_status", handleCaptivePortal); // Android
    webServer.on("/kindle-wifi/wifiredirect.html", handleCaptivePortal); // Kindle/Fire
    
    webServer.onNotFound(handleCaptivePortal); // Catch-all for any other probes
    
    // Start services
    webServer.begin();
    dnsServer.start(53, "*", WiFi.softAPIP());
    
    // Pre-scan WiFi networks so the first page load is instant
    scannedNetworks = WiFi.scanNetworks();
    for (int i = 0; i < scannedNetworks && i < 20; i++) {
        scannedSSID[i] = WiFi.SSID(i);
    }
    
    Serial.println("Provisioning Mode Started: " + apName);
    Serial.println("Accessible at: http://www.cavlineglobal.com");
}


// Loop Provisioning Mode
void loopProvisioning() {
    dnsServer.processNextRequest();
    webServer.handleClient();
}

